import {Schema, model} from 'pg';

const productSchema = new Schema({
    name: String,
    stock: Number,
    unit_price: Number,
    unit_measure: String,
    category: Number,
    total_purchases: Number,
    date_expiration: Date,
    image_url: String,
},{
    timestamps: true,
    versionKey: false
});

export default model('Product', productSchema);

